-- =============================================
-- TIER 2 POWERS (2nd Level Spells)
-- =============================================
INSERT INTO compendium_powers (name, power_level, casting_time, range, duration, description, concentration, school, source_book) VALUES
('Aid', 2, '1 action', '30 feet', '8 hours', 'Your power bolsters your allies with toughness and resolve. Choose up to three creatures within range. Each target''s hit point maximum and current hit points increase by 5 for the duration. At Higher Tiers: Increase by 5 for each tier above 2nd.', false, 'Abjuration', 'SRD'),
('Alter Self', 2, '1 action', 'Self', 'Concentration, up to 1 hour', 'You assume a different form. When you cast this power, choose one of the following options: Aquatic Adaptation, Change Appearance, or Natural Weapons.', true, 'Transmutation', 'SRD'),
('Barkskin', 2, '1 action', 'Touch', 'Concentration, up to 1 hour', 'You touch a willing creature. Until the power ends, the target''s skin has a rough, bark-like appearance, and the target''s AC can''t be less than 16, regardless of what kind of armor it is wearing.', true, 'Transmutation', 'SRD'),
('Blindness/Deafness', 2, '1 action', '30 feet', '1 minute', 'You can blind or deafen a foe. Choose one creature that you can see within range to make a Constitution saving throw. If it fails, the target is either blinded or deafened (your choice) for the duration.', false, 'Necromancy', 'SRD'),
('Blur', 2, '1 action', 'Self', 'Concentration, up to 1 minute', 'Your body becomes blurred, shifting and wavering to all who can see you. For the duration, any creature has disadvantage on attack rolls against you.', true, 'Illusion', 'SRD'),
('Calm Emotions', 2, '1 action', '60 feet', 'Concentration, up to 1 minute', 'You attempt to suppress strong emotions in a group of people. Each humanoid in a 20-foot-radius sphere centered on a point you choose within range must make a Charisma saving throw.', true, 'Enchantment', 'SRD'),
('Continual Flame', 2, '1 action', 'Touch', 'Until dispelled', 'A flame, equivalent in brightness to a torch, springs forth from an object that you touch. The effect looks like a regular flame, but it creates no heat and doesn''t use oxygen.', false, 'Evocation', 'SRD'),
('Darkness', 2, '1 action', '60 feet', 'Concentration, up to 10 minutes', 'Magical darkness spreads from a point you choose within range to fill a 15-foot-radius sphere for the duration. A creature with darkvision can''t see through this darkness.', true, 'Evocation', 'SRD'),
('Darkvision', 2, '1 action', 'Touch', '8 hours', 'You touch a willing creature to grant it the ability to see in the dark. For the duration, that creature has darkvision out to a range of 60 feet.', false, 'Transmutation', 'SRD'),
('Detect Thoughts', 2, '1 action', 'Self', 'Concentration, up to 1 minute', 'For the duration, you can read the thoughts of certain creatures. When you cast the power and as your action on each turn until the power ends, you can focus your mind on any one creature that you can see within 30 feet.', true, 'Divination', 'SRD'),
('Enhance Ability', 2, '1 action', 'Touch', 'Concentration, up to 1 hour', 'You touch a creature and bestow upon it a magical enhancement. Choose one of the following effects: Bear''s Endurance, Bull''s Strength, Cat''s Grace, Eagle''s Splendor, Fox''s Cunning, or Owl''s Wisdom.', true, 'Transmutation', 'SRD'),
('Enlarge/Reduce', 2, '1 action', '30 feet', 'Concentration, up to 1 minute', 'You cause a creature or an object you can see within range to grow larger or smaller for the duration. The target''s size doubles or halves in all dimensions.', true, 'Transmutation', 'SRD'),
('Flaming Sphere', 2, '1 action', '60 feet', 'Concentration, up to 1 minute', 'A 5-foot-diameter sphere of fire appears in an unoccupied space of your choice within range and lasts for the duration. Any creature that ends its turn within 5 feet of the sphere must make a Dexterity saving throw, taking 2d6 fire damage on a failed save.', true, 'Conjuration', 'SRD'),
('Gentle Repose', 2, '1 action', 'Touch', '10 days', 'You touch a corpse or other remains. For the duration, the target is protected from decay and can''t become undead.', false, 'Necromancy', 'SRD'),
('Gust of Wind', 2, '1 action', 'Self (60-foot line)', 'Concentration, up to 1 minute', 'A line of strong wind 60 feet long and 10 feet wide blasts from you in a direction you choose for the power''s duration. Each creature that starts its turn in the line must succeed on a Strength saving throw or be pushed 15 feet away.', true, 'Evocation', 'SRD'),
('Heat Metal', 2, '1 action', '60 feet', 'Concentration, up to 1 minute', 'Choose a manufactured metal object, such as a metal weapon or a suit of metal armor, that you can see within range. You cause the object to glow red-hot. Any creature in physical contact with the object takes 2d8 fire damage.', true, 'Transmutation', 'SRD'),
('Hold Person', 2, '1 action', '60 feet', 'Concentration, up to 1 minute', 'Choose a humanoid that you can see within range. The target must succeed on a Wisdom saving throw or be paralyzed for the duration. At the end of each of its turns, the target can make another Wisdom saving throw.', true, 'Enchantment', 'SRD'),
('Invisibility', 2, '1 action', 'Touch', 'Concentration, up to 1 hour', 'A creature you touch becomes invisible until the power ends. Anything the target is wearing or carrying is invisible as long as it is on the target''s person. The power ends for a target that attacks or casts a power.', true, 'Illusion', 'SRD'),
('Knock', 2, '1 action', '60 feet', 'Instantaneous', 'Choose an object that you can see within range. The object can be a door, a box, a chest, a set of manacles, a padlock, or another object that contains a mundane or magical means that prevents access. A target that is held shut by a mundane lock or that is stuck or barred becomes unlocked.', false, 'Transmutation', 'SRD'),
('Lesser Restoration', 2, '1 action', 'Touch', 'Instantaneous', 'You touch a creature and can end either one disease or one condition afflicting it. The condition can be blinded, deafened, paralyzed, or poisoned.', false, 'Abjuration', 'SRD'),
('Levitate', 2, '1 action', '60 feet', 'Concentration, up to 10 minutes', 'One creature or object of your choice that you can see within range rises vertically, up to 20 feet, and remains suspended there for the duration. The target can move only by pushing or pulling against a fixed object or surface.', true, 'Transmutation', 'SRD'),
('Locate Object', 2, '1 action', 'Self', 'Concentration, up to 10 minutes', 'Describe or name an object that is familiar to you. You sense the direction to the object''s location, as long as that object is within 1,000 feet of you.', true, 'Divination', 'SRD'),
('Magic Weapon', 2, '1 bonus action', 'Touch', 'Concentration, up to 1 hour', 'You touch a nonmagical weapon. Until the power ends, that weapon becomes a magic weapon with a +1 bonus to attack rolls and damage rolls. At Higher Tiers: The bonus increases.', true, 'Transmutation', 'SRD'),
('Melf''s Acid Arrow', 2, '1 action', '90 feet', 'Instantaneous', 'A shimmering green arrow streaks toward a target within range and bursts in a spray of acid. Make a ranged spell attack. On a hit, the target takes 4d4 acid damage immediately and 2d4 acid damage at the end of its next turn.', false, 'Evocation', 'SRD'),
('Mirror Image', 2, '1 action', 'Self', '1 minute', 'Three illusory duplicates of yourself appear in your space. Until the power ends, the duplicates move with you and mimic your actions. Each time a creature targets you with an attack during the power''s duration, roll a d20 to determine whether the attack instead targets one of your duplicates.', false, 'Illusion', 'SRD'),
('Misty Step', 2, '1 bonus action', 'Self', 'Instantaneous', 'Briefly surrounded by silvery mist, you teleport up to 30 feet to an unoccupied space that you can see.', false, 'Conjuration', 'SRD'),
('Moonbeam', 2, '1 action', '120 feet', 'Concentration, up to 1 minute', 'A silvery beam of pale light shines down in a 5-foot-radius, 40-foot-high cylinder centered on a point within range. Until the power ends, dim light fills the cylinder. When a creature enters the power''s area for the first time on a turn or starts its turn there, it makes a Constitution saving throw, taking 2d10 radiant damage on a failed save.', true, 'Evocation', 'SRD'),
('Pass without Trace', 2, '1 action', 'Self', 'Concentration, up to 1 hour', 'A veil of shadows and silence radiates from you, masking you and your companions from detection. For the duration, each creature you choose within 30 feet has a +10 bonus to Stealth checks and can''t be tracked except by magical means.', true, 'Abjuration', 'SRD'),
('Prayer of Healing', 2, '10 minutes', '30 feet', 'Instantaneous', 'Up to six creatures of your choice that you can see within range each regain hit points equal to 2d8 + your spellcasting ability modifier. At Higher Tiers: The healing increases by 1d8 for each tier above 2nd.', false, 'Evocation', 'SRD'),
('Protection from Poison', 2, '1 action', 'Touch', '1 hour', 'You touch a creature. If it is poisoned, you neutralize the poison. For the duration, the target has advantage on saving throws against being poisoned, and it has resistance to poison damage.', false, 'Abjuration', 'SRD'),
('Ray of Enfeeblement', 2, '1 action', '60 feet', 'Concentration, up to 1 minute', 'A black beam of enervating energy springs from your finger toward a creature within range. Make a ranged spell attack against the target. On a hit, the target deals only half damage with weapon attacks that use Strength until the power ends.', true, 'Necromancy', 'SRD'),
('Scorching Ray', 2, '1 action', '120 feet', 'Instantaneous', 'You create three rays of fire and hurl them at targets within range. You can hurl them at one target or several. Make a ranged spell attack for each ray. On a hit, the target takes 2d6 fire damage. At Higher Tiers: Create one additional ray for each tier above 2nd.', false, 'Evocation', 'SRD'),
('See Invisibility', 2, '1 action', 'Self', '1 hour', 'For the duration, you see invisible creatures and objects as if they were visible, and you can see into the Ethereal Plane.', false, 'Divination', 'SRD'),
('Shatter', 2, '1 action', '60 feet', 'Instantaneous', 'A sudden loud ringing noise, painfully intense, erupts from a point of your choice within range. Each creature in a 10-foot-radius sphere centered on that point must make a Constitution saving throw. A creature takes 3d8 thunder damage on a failed save, or half as much on a success.', false, 'Evocation', 'SRD'),
('Silence', 2, '1 action', '120 feet', 'Concentration, up to 10 minutes', 'For the duration, no sound can be created within or pass through a 20-foot-radius sphere centered on a point you choose within range. Any creature or object entirely inside the sphere is immune to thunder damage, and creatures are deafened while entirely inside it.', true, 'Illusion', 'SRD'),
('Spider Climb', 2, '1 action', 'Touch', 'Concentration, up to 1 hour', 'Until the power ends, one willing creature you touch gains the ability to move up, down, and across vertical surfaces and upside down along ceilings, while leaving its hands free. The target also gains a climbing speed equal to its walking speed.', true, 'Transmutation', 'SRD'),
('Spiritual Weapon', 2, '1 bonus action', '60 feet', '1 minute', 'You create a floating, spectral weapon within range that lasts for the duration or until you cast this power again. When you cast the power, you can make a melee spell attack against a creature within 5 feet of the weapon. On a hit, the target takes force damage equal to 1d8 + your spellcasting ability modifier.', false, 'Evocation', 'SRD'),
('Suggestion', 2, '1 action', '30 feet', 'Concentration, up to 8 hours', 'You suggest a course of activity (limited to a sentence or two) and magically influence a creature you can see within range that can hear and understand you. The suggestion must be worded in such a manner as to make the course of action sound reasonable.', true, 'Enchantment', 'SRD'),
('Web', 2, '1 action', '60 feet', 'Concentration, up to 1 hour', 'You conjure a mass of thick, sticky webbing at a point of your choice within range. The webs fill a 20-foot cube from that point for the duration. The webs are difficult terrain and lightly obscure their area.', true, 'Conjuration', 'SRD'),
('Zone of Truth', 2, '1 action', '60 feet', '10 minutes', 'You create a magical zone that guards against deception in a 15-foot-radius sphere centered on a point of your choice within range. Until the power ends, a creature that enters the power''s area for the first time on a turn or starts its turn there must make a Charisma saving throw.', false, 'Enchantment', 'SRD'),

-- =============================================
-- TIER 3 POWERS (3rd Level Spells)
-- =============================================
('Animate Dead', 3, '1 minute', '10 feet', 'Instantaneous', 'This power creates an undead servant. Choose a pile of bones or a corpse of a humanoid within range. Your power imbues the target with a foul mimicry of life, raising it as an undead creature. The creature is under your control for 24 hours.', false, 'Necromancy', 'SRD'),
('Beacon of Hope', 3, '1 action', '30 feet', 'Concentration, up to 1 minute', 'This power bestows hope and vitality. Choose any number of creatures within range. For the duration, each target has advantage on Wisdom saving throws and death saving throws, and regains the maximum number of hit points possible from any healing.', true, 'Abjuration', 'SRD'),
('Bestow Curse', 3, '1 action', 'Touch', 'Concentration, up to 1 minute', 'You touch a creature, and that creature must succeed on a Wisdom saving throw or become cursed for the duration of the power. When you cast this power, choose the nature of the curse.', true, 'Necromancy', 'SRD'),
('Blink', 3, '1 action', 'Self', '1 minute', 'Roll a d20 at the end of each of your turns for the duration of the power. On a roll of 11 or higher, you vanish from your current plane of existence and appear in the Ethereal Plane until the start of your next turn.', false, 'Transmutation', 'SRD'),
('Call Lightning', 3, '1 action', '120 feet', 'Concentration, up to 10 minutes', 'A storm cloud appears in the shape of a cylinder that is 10 feet tall with a 60-foot radius, centered on a point you can see 100 feet directly above you. When you cast the power, choose a point you can see within range. A bolt of lightning flashes down from the cloud to that point. Each creature within 5 feet of that point must make a Dexterity saving throw. A creature takes 3d10 lightning damage on a failed save.', true, 'Conjuration', 'SRD'),
('Clairvoyance', 3, '10 minutes', '1 mile', 'Concentration, up to 10 minutes', 'You create an invisible sensor within range in a location familiar to you or in an obvious location that is unfamiliar to you. The sensor remains in place for the duration, and it can''t be attacked or otherwise interacted with.', true, 'Divination', 'SRD'),
('Counterspell', 3, '1 reaction', '60 feet', 'Instantaneous', 'You attempt to interrupt a creature in the process of casting a power. If the creature is casting a power of tier 3 or lower, its power fails and has no effect. If it is casting a power of tier 4 or higher, make an ability check.', false, 'Abjuration', 'SRD'),
('Create Food and Water', 3, '1 action', '30 feet', 'Instantaneous', 'You create 45 pounds of food and 30 gallons of water on the ground or in containers within range, enough to sustain up to fifteen humanoids or five steeds for 24 hours.', false, 'Conjuration', 'SRD'),
('Daylight', 3, '1 action', '60 feet', '1 hour', 'A 60-foot-radius sphere of light spreads out from a point you choose within range. The sphere is bright light and sheds dim light for an additional 60 feet.', false, 'Evocation', 'SRD'),
('Dispel Magic', 3, '1 action', '120 feet', 'Instantaneous', 'Choose one creature, object, or magical effect within range. Any power of tier 3 or lower on the target ends. For each power of tier 4 or higher on the target, make an ability check.', false, 'Abjuration', 'SRD'),
('Fear', 3, '1 action', 'Self (30-foot cone)', 'Concentration, up to 1 minute', 'You project a phantasmal image of a creature''s worst fears. Each creature in a 30-foot cone must succeed on a Wisdom saving throw or drop whatever it is holding and become frightened for the duration.', true, 'Illusion', 'SRD'),
('Fly', 3, '1 action', 'Touch', 'Concentration, up to 10 minutes', 'You touch a willing creature. The target gains a flying speed of 60 feet for the duration. When the power ends, the target falls if it is still aloft.', true, 'Transmutation', 'SRD'),
('Gaseous Form', 3, '1 action', 'Touch', 'Concentration, up to 1 hour', 'You transform a willing creature you touch, along with everything it''s wearing and carrying, into a misty cloud for the duration. The power ends if the creature drops to 0 hit points.', true, 'Transmutation', 'SRD'),
('Glyph of Warding', 3, '1 hour', 'Touch', 'Until dispelled or triggered', 'When you cast this power, you inscribe a glyph that harms other creatures, either upon a surface or within an object that can be closed. The glyph is nearly invisible. If you choose a surface, the glyph can cover an area no larger than 10 feet in diameter.', false, 'Abjuration', 'SRD'),
('Haste', 3, '1 action', '30 feet', 'Concentration, up to 1 minute', 'Choose a willing creature that you can see within range. Until the power ends, the target''s speed is doubled, it gains a +2 bonus to AC, it has advantage on Dexterity saving throws, and it gains an additional action on each of its turns.', true, 'Transmutation', 'SRD'),
('Hypnotic Pattern', 3, '1 action', '120 feet', 'Concentration, up to 1 minute', 'You create a twisting pattern of colors that weaves through the air inside a 30-foot cube within range. The pattern appears for a moment and vanishes. Each creature in the area who sees the pattern must make a Wisdom saving throw.', true, 'Illusion', 'SRD'),
('Lightning Bolt', 3, '1 action', 'Self (100-foot line)', 'Instantaneous', 'A stroke of lightning forming a line 100 feet long and 5 feet wide blasts out from you in a direction you choose. Each creature in the line must make a Dexterity saving throw. A creature takes 8d6 lightning damage on a failed save, or half as much on a success.', false, 'Evocation', 'SRD'),
('Magic Circle', 3, '1 minute', '10 feet', '1 hour', 'You create a 10-foot-radius, 20-foot-tall cylinder of magical energy centered on a point on the ground that you can see within range. The cylinder has specific effects on creatures of a chosen type.', false, 'Abjuration', 'SRD'),
('Major Image', 3, '1 action', '120 feet', 'Concentration, up to 10 minutes', 'You create the image of an object, a creature, or some other visible phenomenon that is no larger than a 20-foot cube. The image appears at a spot that you can see within range and lasts for the duration. It seems completely real.', true, 'Illusion', 'SRD'),
('Mass Healing Word', 3, '1 bonus action', '60 feet', 'Instantaneous', 'As you call out words of restoration, up to six creatures of your choice that you can see within range regain hit points equal to 1d4 + your spellcasting ability modifier.', false, 'Evocation', 'SRD'),
('Meld into Stone', 3, '1 action', 'Touch', '8 hours', 'You step into a stone object or surface large enough to fully contain your body, melding yourself and all the equipment you carry with the stone for the duration.', false, 'Transmutation', 'SRD'),
('Nondetection', 3, '1 action', 'Touch', '8 hours', 'For the duration, you hide a target that you touch from divination magic. The target can be a willing creature or a place or an object no larger than 10 feet in any dimension.', false, 'Abjuration', 'SRD'),
('Phantom Steed', 3, '1 minute', '30 feet', '1 hour', 'A Large quasi-real, horselike creature appears on the ground in an unoccupied space of your choice within range. You decide the creature''s appearance. The creature has a speed of 100 feet.', false, 'Illusion', 'SRD'),
('Plant Growth', 3, '1 action or 8 hours', '150 feet', 'Instantaneous', 'This power channels vitality into plants within a specific area. There are two possible uses for the power, granting either immediate or long-term benefits.', false, 'Transmutation', 'SRD'),
('Protection from Energy', 3, '1 action', 'Touch', 'Concentration, up to 1 hour', 'For the duration, the willing creature you touch has resistance to one damage type of your choice: acid, cold, fire, lightning, or thunder.', true, 'Abjuration', 'SRD'),
('Remove Curse', 3, '1 action', 'Touch', 'Instantaneous', 'At your touch, all curses affecting one creature or object end. If the object is a cursed magic item, its curse remains, but the power breaks its owner''s attunement to the object so it can be removed or discarded.', false, 'Abjuration', 'SRD'),
('Revivify', 3, '1 action', 'Touch', 'Instantaneous', 'You touch a creature that has died within the last minute. That creature returns to life with 1 hit point. This power can''t return to life a creature that has died of old age, nor can it restore any missing body parts.', false, 'Necromancy', 'SRD'),
('Sending', 3, '1 action', 'Unlimited', '1 round', 'You send a short message of twenty-five words or less to a creature with which you are familiar. The creature hears the message in its mind, recognizes you as the sender, and can answer in a like manner immediately.', false, 'Evocation', 'SRD'),
('Slow', 3, '1 action', '120 feet', 'Concentration, up to 1 minute', 'You alter time around up to six creatures of your choice in a 40-foot cube within range. Each target must succeed on a Wisdom saving throw or be affected by this power for the duration.', true, 'Transmutation', 'SRD'),
('Speak with Dead', 3, '1 action', '10 feet', '10 minutes', 'You grant the semblance of life and intelligence to a corpse of your choice within range, allowing it to answer the questions you pose. The corpse must still have a mouth and can''t be undead.', false, 'Necromancy', 'SRD'),
('Spirit Guardians', 3, '1 action', 'Self (15-foot radius)', 'Concentration, up to 10 minutes', 'You call forth spirits to protect you. They flit around you to a distance of 15 feet for the duration. When a creature enters the area for the first time on a turn or starts its turn there, it must make a Wisdom saving throw. On a failed save, the creature takes 3d8 radiant damage.', true, 'Conjuration', 'SRD'),
('Stinking Cloud', 3, '1 action', '90 feet', 'Concentration, up to 1 minute', 'You create a 20-foot-radius sphere of yellow, nauseating gas centered on a point within range. The cloud spreads around corners, and its area is heavily obscured. Each creature that is completely within the cloud at the start of its turn must make a Constitution saving throw against poison.', true, 'Conjuration', 'SRD'),
('Tongues', 3, '1 action', 'Touch', '1 hour', 'This power grants the creature you touch the ability to understand any spoken language it hears. Moreover, when the target speaks, any creature that knows at least one language and can hear the target understands what it says.', false, 'Divination', 'SRD'),
('Vampiric Touch', 3, '1 action', 'Self', 'Concentration, up to 1 minute', 'The touch of your shadow-wreathed hand can siphon life force from others to heal your wounds. Make a melee spell attack against a creature within your reach. On a hit, the target takes 3d6 necrotic damage, and you regain hit points equal to half the amount of necrotic damage dealt.', true, 'Necromancy', 'SRD'),
('Water Breathing', 3, '1 action', '30 feet', '24 hours', 'This power grants up to ten willing creatures you can see within range the ability to breathe underwater until the power ends. Affected creatures also retain their normal mode of respiration.', false, 'Transmutation', 'SRD'),
('Water Walk', 3, '1 action', '30 feet', '1 hour', 'This power grants the ability to move across any liquid surface—such as water, acid, mud, snow, quicksand, or lava—as if it were harmless solid ground. Up to ten willing creatures you can see within range gain this ability.', false, 'Transmutation', 'SRD'),
('Wind Wall', 3, '1 action', '120 feet', 'Concentration, up to 1 minute', 'A wall of strong wind rises from the ground at a point you choose within range. The wall is up to 50 feet long, 15 feet high, and 1 foot thick. The wall lasts for the duration.', true, 'Evocation', 'SRD')
ON CONFLICT DO NOTHING;