INSERT INTO compendium_feats (name, description, prerequisites, benefits, tags, source_book) VALUES
('Alert', 'Always on the lookout for danger, you gain the following benefits.', NULL, ARRAY['+5 bonus to initiative', 'Cannot be surprised while conscious', 'Hidden creatures do not gain advantage on attacks against you'], ARRAY['combat', 'awareness'], 'SRD'),
('Athlete', 'You have undergone extensive physical training.', NULL, ARRAY['Increase STR or AGI by 1 (max 20)', 'Standing from prone uses only 5 feet of movement', 'Climbing does not cost extra movement', 'Running jump after only 5 feet of movement'], ARRAY['physical', 'mobility'], 'SRD'),
('Actor', 'Skilled at mimicry and dramatics.', NULL, ARRAY['Increase PRE by 1 (max 20)', 'Advantage on Deception and Performance checks when passing as another', 'Mimic speech or sounds of creatures you have heard'], ARRAY['social', 'deception'], 'SRD'),
('Charger', 'When you Dash, you can make a bonus melee attack or shove.', NULL, ARRAY['If you move 10+ feet in straight line before attack, gain +5 damage bonus or push target 10 feet'], ARRAY['combat', 'mobility'], 'SRD'),
('Crossbow Expert', 'Extensive practice with crossbows.', NULL, ARRAY['Ignore loading property of crossbows you are proficient with', 'No disadvantage for ranged attacks within 5 feet', 'When you Attack with one-handed weapon, bonus action crossbow attack'], ARRAY['combat', 'ranged'], 'SRD'),
('Defensive Duelist', 'When wielding a finesse weapon, you can parry.', 'AGI 13+', ARRAY['When hit by melee attack, use reaction to add proficiency bonus to AC for that attack'], ARRAY['combat', 'defense'], 'SRD'),
('Dual Wielder', 'Master of fighting with two weapons.', NULL, ARRAY['+1 AC while wielding separate melee weapons in each hand', 'Use two-weapon fighting with non-light weapons', 'Draw or stow two weapons instead of one'], ARRAY['combat', 'dual-wield'], 'SRD'),
('Dungeon Delver', 'Expert at navigating dangerous underground areas.', NULL, ARRAY['Advantage on Sense checks to detect secret doors', 'Advantage on saves vs traps', 'Resistance to trap damage', 'Search for traps at normal pace'], ARRAY['exploration', 'utility'], 'SRD'),
('Durable', 'Hardy and resilient.', NULL, ARRAY['Increase VIT by 1 (max 20)', 'When rolling Hit Dice, minimum roll equals twice VIT modifier'], ARRAY['survival', 'healing'], 'SRD'),
('Elemental Adept', 'Your powers ignore resistance to a chosen element.', 'Ability to use at least one power', ARRAY['Choose acid, cold, fire, lightning, or thunder', 'Powers ignore resistance to chosen type', 'Treat 1s on damage dice as 2s'], ARRAY['power', 'elemental'], 'SRD'),
('Grappler', 'Expert at close combat grappling.', 'STR 13+', ARRAY['Advantage on attacks vs creatures you are grappling', 'Use action to pin grappled creature (both restrained)'], ARRAY['combat', 'grapple'], 'SRD'),
('Great Weapon Master', 'Devastating attacks with heavy weapons.', NULL, ARRAY['On critical or reducing creature to 0 HP, bonus action melee attack', 'Before attack with heavy weapon, take -5 to hit for +10 damage'], ARRAY['combat', 'damage'], 'SRD'),
('Healer', 'Expert at treating wounds.', NULL, ARRAY['Use healers kit to stabilize and restore 1 HP', 'Use healers kit action to restore 1d6+4+creature HD HP (once per rest per creature)'], ARRAY['healing', 'support'], 'SRD'),
('Heavily Armored', 'Trained with heavy armor.', 'Medium armor proficiency', ARRAY['Increase STR by 1 (max 20)', 'Gain proficiency with heavy armor'], ARRAY['armor', 'tank'], 'SRD'),
('Heavy Armor Master', 'Use armor to deflect strikes.', 'Heavy armor proficiency', ARRAY['Increase STR by 1 (max 20)', 'While wearing heavy armor, reduce nonmagical bludgeoning/piercing/slashing by 3'], ARRAY['armor', 'tank', 'damage reduction'], 'SRD'),
('Inspiring Leader', 'Inspire companions to fight harder.', 'PRE 13+', ARRAY['Spend 10 minutes inspiring up to 6 allies', 'Each gains temp HP equal to your level + PRE modifier'], ARRAY['support', 'leadership'], 'SRD'),
('Keen Mind', 'Exceptional memory and awareness.', NULL, ARRAY['Increase INT by 1 (max 20)', 'Always know which way is north', 'Always know hours until sunrise/sunset', 'Accurately recall anything seen/heard in past month'], ARRAY['mental', 'utility'], 'SRD'),
('Lightly Armored', 'Trained with light armor.', NULL, ARRAY['Increase STR or AGI by 1 (max 20)', 'Gain proficiency with light armor'], ARRAY['armor', 'training'], 'SRD'),
('Linguist', 'Studied languages and codes.', NULL, ARRAY['Increase INT by 1 (max 20)', 'Learn 3 languages', 'Create written ciphers others cannot decipher without check'], ARRAY['mental', 'communication'], 'SRD'),
('Lucky', 'Inexplicable luck seems to favor you.', NULL, ARRAY['3 luck points per long rest', 'Spend point to reroll attack, ability check, or saving throw', 'Spend point to force reroll of attack against you'], ARRAY['utility', 'luck'], 'SRD'),
('Mage Slayer', 'Practiced techniques against spellcasters.', NULL, ARRAY['When creature within 5 feet casts power, reaction melee attack', 'Advantage on saves vs powers from adjacent creatures', 'Concentration checks from your damage have disadvantage'], ARRAY['combat', 'anti-mage'], 'SRD'),
('Magic Initiate', 'Learn basic powers from another job.', NULL, ARRAY['Choose a job: learn 2 cantrips and one Tier 1 power from that job', 'Use Tier 1 power once per long rest without slot'], ARRAY['power', 'versatility'], 'SRD'),
('Martial Adept', 'Trained in combat maneuvers.', NULL, ARRAY['Learn 2 maneuvers from Battle Master', 'Gain 1 superiority die (d6)', 'Die regains on short/long rest'], ARRAY['combat', 'maneuvers'], 'SRD'),
('Medium Armor Master', 'Expert with medium armor.', 'Medium armor proficiency', ARRAY['Medium armor does not impose stealth disadvantage', 'AGI modifier can be +3 instead of +2 in medium armor'], ARRAY['armor', 'stealth'], 'SRD'),
('Mobile', 'Exceptionally speedy and agile.', NULL, ARRAY['Speed increases by 10 feet', 'Dash through difficult terrain without extra cost', 'Melee attack against creature means no opportunity attack from them'], ARRAY['mobility', 'combat'], 'SRD'),
('Moderately Armored', 'Trained with medium armor.', 'Light armor proficiency', ARRAY['Increase STR or AGI by 1 (max 20)', 'Gain proficiency with medium armor and shields'], ARRAY['armor', 'training'], 'SRD'),
('Mounted Combatant', 'Expert at mounted combat.', NULL, ARRAY['Advantage on attacks vs unmounted creatures smaller than mount', 'Force attacks targeting mount to target you instead', 'Mount takes no damage on successful AGI save (half on fail)'], ARRAY['combat', 'mounted'], 'SRD'),
('Observant', 'Quick to notice details.', NULL, ARRAY['Increase INT or SENSE by 1 (max 20)', '+5 to passive Perception and Investigation', 'Read lips if you can see mouth and know language'], ARRAY['perception', 'awareness'], 'SRD'),
('Polearm Master', 'Expert with polearm weapons.', NULL, ARRAY['Bonus action attack with opposite end (1d4 bludgeoning)', 'Creatures entering your reach provoke opportunity attacks'], ARRAY['combat', 'reach'], 'SRD'),
('Resilient', 'Developed resilience to certain effects.', NULL, ARRAY['Increase one ability by 1 (max 20)', 'Gain proficiency in saving throws with that ability'], ARRAY['saves', 'defense'], 'SRD'),
('Ritual Caster', 'Learned ritual powers from another job.', 'INT or SENSE 13+', ARRAY['Acquire ritual book with two Tier 1 ritual powers', 'Copy ritual powers you find into book', 'Cast rituals from book'], ARRAY['power', 'ritual'], 'SRD'),
('Savage Attacker', 'Devastating melee attacks.', NULL, ARRAY['Once per turn, reroll melee weapon damage and use either result'], ARRAY['combat', 'damage'], 'SRD'),
('Sentinel', 'Master at protecting allies.', NULL, ARRAY['Creatures you hit with opportunity attacks have speed reduced to 0', 'Creatures within 5 feet provoke opportunity attacks even if Disengage', 'Reaction attack when creature within 5 feet attacks someone else'], ARRAY['combat', 'tank', 'protection'], 'SRD'),
('Sharpshooter', 'Master of ranged weapon attacks.', NULL, ARRAY['Attacking at long range does not impose disadvantage', 'Ranged attacks ignore half and three-quarters cover', 'Before attack, take -5 to hit for +10 damage'], ARRAY['combat', 'ranged', 'damage'], 'SRD'),
('Shield Master', 'Use shields for offense and defense.', NULL, ARRAY['Bonus action shove when you Attack', 'Add shield AC to AGI saves vs single-target effects', 'No damage on successful AGI save (half damage otherwise)'], ARRAY['combat', 'tank', 'shield'], 'SRD'),
('Skilled', 'Exceptional aptitude for many tasks.', NULL, ARRAY['Gain proficiency in any combination of 3 skills or tools'], ARRAY['skills', 'versatility'], 'SRD'),
('Skulker', 'Expert at hiding.', 'AGI 13+', ARRAY['Hide when lightly obscured', 'Missing ranged attack does not reveal position', 'Dim light does not impose disadvantage on Perception'], ARRAY['stealth', 'ranged'], 'SRD'),
('Spell Sniper', 'Enhanced ranged power attacks.', 'Ability to use at least one power', ARRAY['Double range of attack powers', 'Ranged power attacks ignore half and three-quarters cover', 'Learn one attack cantrip'], ARRAY['power', 'ranged'], 'SRD'),
('Tavern Brawler', 'Expert at improvised fighting.', NULL, ARRAY['Increase STR or VIT by 1 (max 20)', 'Proficient with improvised weapons and unarmed strikes', 'Unarmed strikes deal 1d4', 'Bonus action grapple when you hit with unarmed/improvised'], ARRAY['combat', 'grapple', 'improvised'], 'SRD'),
('Tough', 'Exceptionally hardy.', NULL, ARRAY['HP maximum increases by 2 per level'], ARRAY['survival', 'tank', 'hp'], 'SRD'),
('War Caster', 'Practiced casting in combat.', 'Ability to use at least one power', ARRAY['Advantage on CON saves for concentration', 'Perform somatic components with hands full', 'Cast power as opportunity attack'], ARRAY['power', 'combat', 'concentration'], 'SRD'),
('Weapon Master', 'Extensive weapon training.', NULL, ARRAY['Increase STR or AGI by 1 (max 20)', 'Gain proficiency with 4 weapons of your choice'], ARRAY['combat', 'weapons', 'training'], 'SRD'),
INSERT INTO compendium_backgrounds (name, description, skill_proficiencies, tool_proficiencies, language_count, starting_equipment, starting_credits, feature_name, feature_description, personality_traits, ideals, bonds, flaws, tags, source_book) VALUES
('Acolyte', 'You have spent your life in service to a temple or religious institution, learning sacred rites and providing sacrifices.', ARRAY['Insight', 'Religion'], NULL, 2, 'Holy symbol, prayer book, vestments, 5 sticks of incense', 1500, 'Shelter of the Faithful', 'You and your companions can receive free healing and care at temples of your faith, and supporters will provide modest lifestyle support.', ARRAY['I idolize a particular hero of my faith', 'I can find common ground between the fiercest enemies', 'I see omens in every event', 'Nothing can shake my optimistic attitude'], ARRAY['Tradition: Ancient traditions must be preserved', 'Charity: I always try to help those in need', 'Change: We must adapt to survive the gates', 'Power: I seek to prove the superiority of my faith'], ARRAY['I would die to recover an ancient relic of my faith', 'I owe my life to the priest who took me in', 'I will do anything to protect my temple', 'My faith guides everything I do'], ARRAY['I judge others harshly', 'I am inflexible in my thinking', 'I put too much trust in authority figures', 'Once I pick a goal, I become obsessed'], ARRAY['religious', 'faith'], 'SRD'),
('Charlatan', 'You have always had a way with people. You know what makes them tick, and you can tease out their desires with a few questions.', ARRAY['Deception', 'Sleight of Hand'], ARRAY['Disguise kit', 'Forgery kit'], 0, 'Fine clothes, disguise kit, tools of the con', 1500, 'False Identity', 'You have created a second identity that includes documentation, established acquaintances, and disguises.', ARRAY['I fall in and out of love easily', 'I have a joke for every occasion', 'Flattery is my preferred trick', 'I am a born gambler'], ARRAY['Independence: I am a free spirit', 'Fairness: I never target people who cannot afford to lose', 'Charity: I distribute money I acquire', 'Creativity: I never run the same con twice'], ARRAY['I swindled the wrong person once', 'Someone I loved died because of my mistake', 'I owe everything to my mentor', 'Somewhere there is a child who does not know I am their parent'], ARRAY['I cannot resist a pretty face', 'I turn tail and run when things get bad', 'I am always in debt', 'I cannot resist swindling people'], ARRAY['criminal', 'deception'], 'SRD'),
('Criminal', 'You are an experienced criminal with a history of breaking the law. You have spent time among other criminals and maintain contacts within the underworld.', ARRAY['Deception', 'Stealth'], ARRAY['Gaming set', 'Thieves tools'], 0, 'Crowbar, dark clothes with hood, thieves tools', 1500, 'Criminal Contact', 'You have a reliable contact who acts as a liaison to a network of criminals. You can send and receive messages through this contact.', ARRAY['I always have a plan for when things go wrong', 'I am incredibly slow to trust', 'I do not pay attention to risks', 'The best way to get me to do something is tell me I cannot'], ARRAY['Honor: I do not steal from those in my crew', 'Freedom: Chains are meant to be broken', 'Charity: I steal from the wealthy to help the poor', 'Greed: I will do whatever it takes to become wealthy'], ARRAY['I am trying to pay off an old debt', 'Someone I loved was killed by my crimes', 'My ill-gotten gains go to support my family', 'Something important was taken from me'], ARRAY['When I see something valuable I cannot think about anything else', 'I turn tail and run when things get bad', 'I have a weakness for the pleasures of the city', 'If there is a plan I will forget it'], ARRAY['criminal', 'stealth'], 'SRD'),
('Entertainer', 'You thrive in front of an audience, bringing joy and entertainment wherever you go. You have performed in many venues.', ARRAY['Acrobatics', 'Performance'], ARRAY['Disguise kit', 'Musical instrument'], 0, 'Musical instrument, favor from admirer, costume', 1500, 'By Popular Demand', 'You can always find a place to perform. You receive free lodging and food of a modest standard in exchange for performances.', ARRAY['I know a story relevant to any situation', 'Whenever I come to a new place I collect rumors', 'I am a hopeless romantic', 'Nobody stays angry at me for long'], ARRAY['Beauty: When I perform I make the world better', 'Tradition: The old stories must be told', 'Creativity: The world always needs new ideas', 'Greed: I am only in it for the money and fame'], ARRAY['My instrument is my most treasured possession', 'Someone stole my best song', 'I idolize a famous entertainer', 'I would do anything for fame'], ARRAY['I will do anything to win fame', 'I am a sucker for a pretty face', 'I have trouble keeping my true feelings hidden', 'A scandal prevents me from returning home'], ARRAY['performance', 'social'], 'SRD'),
('Folk Hero', 'You come from humble origins but are destined for greater things. The common people regard you as their champion.', ARRAY['Animal Handling', 'Survival'], ARRAY['Artisan tools', 'Vehicles (land)'], 0, 'Artisan tools, shovel, iron pot, common clothes', 1000, 'Rustic Hospitality', 'Common folk will help you hide, rest, or recuperate. They will shield you from the law but not risk their lives for you.', ARRAY['I judge people by their actions', 'If someone is in trouble I always help', 'I feel far more comfortable around animals', 'I am convinced of the significance of my destiny'], ARRAY['Respect: People deserve respect', 'Fairness: No one is above the law', 'Freedom: Tyrants must be overthrown', 'Might: The strong must protect the weak'], ARRAY['I have a family I would die to protect', 'I worked the land and love it', 'A proud noble once gave me a beating', 'My tools are symbols of my past life'], ARRAY['The tyrant who rules my land will stop at nothing to see me killed', 'I have trouble trusting allies', 'I am convinced of the significance of my destiny', 'I have a weakness for pretty faces'], ARRAY['common', 'heroic'], 'SRD'),
('Guild Artisan', 'You are a member of an artisan guild, skilled in a particular craft. Your guild has trained you and provides connections.', ARRAY['Insight', 'Persuasion'], ARRAY['Artisan tools'], 1, 'Artisan tools, letter of introduction, travelers clothes', 1500, 'Guild Membership', 'Your guild will provide lodging and food. You can call on the guild for assistance and access to powerful political connections.', ARRAY['I believe everyone deserves fair treatment', 'I am horribly jealous of anyone successful', 'I like to talk at length about my profession', 'I prefer quality over everything'], ARRAY['Community: The guild is worth protecting', 'Generosity: My talents were given to me to benefit the world', 'Freedom: Everyone should be free to pursue their livelihood', 'Greed: I am only in it for the money'], ARRAY['I pursue wealth to secure someones love', 'My workshop is the most important place to me', 'I owe my guild a great debt', 'One day I will prove my worth'], ARRAY['I am never satisfied with what I have', 'I overvalue my work', 'I will do anything to get my hands on rare components', 'I am quick to assume people are cheating me'], ARRAY['craft', 'guild'], 'SRD'),
('Hermit', 'You lived in seclusion, either in a sheltered community or entirely alone, for a formative part of your life.', ARRAY['Medicine', 'Religion'], ARRAY['Herbalism kit'], 1, 'Scroll case with notes, winter blanket, herbalism kit, common clothes', 500, 'Discovery', 'You have made a unique and powerful discovery. This truth could have significant impact on the world.', ARRAY['I have been isolated so long I rarely speak', 'I am utterly serene even in disaster', 'I connect everything to a grand cosmic plan', 'I often get lost in my own thoughts'], ARRAY['Greater Good: My gifts are meant for all', 'Logic: Emotions cloud judgment', 'Free Thinking: Inquiry must go unchallenged', 'Self-Knowledge: Know yourself truly'], ARRAY['I am searching for enlightenment', 'I must protect the place of my seclusion', 'I entered seclusion to hide from those who hunt me', 'I am working on a great philosophical work'], ARRAY['Now that I have returned I enjoy pleasures too much', 'I harbor dark thoughts that isolation could not squash', 'I am dogmatic in my beliefs', 'I let my need for answers drive my decisions'], ARRAY['isolated', 'spiritual'], 'SRD'),
('Noble', 'You were born to privilege and power. Your family owns land, collects taxes, and wields significant political influence.', ARRAY['History', 'Persuasion'], ARRAY['Gaming set'], 1, 'Fine clothes, signet ring, scroll of pedigree', 2500, 'Position of Privilege', 'You are welcome in high society. Common folk make every effort to accommodate you and avoid your displeasure.', ARRAY['My eloquent flattery makes everyone feel important', 'The common folk love me for my kindness', 'No one could doubt that I am a cut above the common rabble', 'I take great pains with my appearance'], ARRAY['Respect: Respect is due to everyone', 'Responsibility: It is my duty to respect authority', 'Independence: I must prove I can handle myself', 'Power: If I can attain power no one will tell me what to do'], ARRAY['I will face any challenge to win approval', 'My houses alliance with another is paramount', 'Nothing is more important than my family', 'I am in love with the heir of a rival house'], ARRAY['I secretly believe everyone is beneath me', 'I hide a shameful secret', 'I too often hear veiled insults', 'I have an insatiable desire for pleasures'], ARRAY['noble', 'political'], 'SRD'),
('Outlander', 'You grew up in the wilds, far from civilization. You have witnessed the migration of herds and survived extreme conditions.', ARRAY['Athletics', 'Survival'], ARRAY['Musical instrument'], 1, 'Staff, hunting trap, trophy from animal, travelers clothes', 1000, 'Wanderer', 'You have an excellent memory for maps and geography. You can always recall the layout of terrain and can find food and fresh water for yourself and up to five others.', ARRAY['I am driven by wanderlust', 'I watch over my friends as if they were my litter', 'I was actually raised by wolves', 'I have a lesson for every situation'], ARRAY['Change: Life is ever changing', 'Greater Good: It is our duty to help others', 'Honor: I would rather die than break my word', 'Might: The strongest survive'], ARRAY['My family or tribe is the most important thing', 'An injury to the unspoiled wilderness is an injury to me', 'I will bring terrible wrath on evildoers', 'I am the last of my tribe'], ARRAY['I am too enamored of ale', 'There is no room for caution in a life lived fully', 'I remember every insult', 'I am slow to trust civilization'], ARRAY['wilderness', 'survival'], 'SRD'),
('Sage', 'You spent years learning the lore of the multiverse. You have pored through manuscripts and studied under experts.', ARRAY['Arcana', 'History'], NULL, 2, 'Bottle of ink, quill, small knife, letter from colleague, common clothes', 1000, 'Researcher', 'When you attempt to learn or recall lore, if you do not know the information, you often know where to find it.', ARRAY['I use polysyllabic words', 'I have read every book in the greatest libraries', 'I am willing to hear every side of an argument', 'I speak without thinking about my words'], ARRAY['Knowledge: The path to power is knowledge', 'Beauty: Art and beauty are transcendent', 'Logic: Emotions must not cloud judgment', 'No Limits: Nothing should limit the possibilities'], ARRAY['I seek to recover lost knowledge', 'My lifes work is a series of tomes', 'I seek to discover what destroyed my library', 'I have been searching my whole life for answers'], ARRAY['I overlook obvious solutions', 'I speak without considering consequences', 'Unlocking secrets is worth any price', 'Most people scream and run when they see a demon'], ARRAY['scholar', 'knowledge'], 'SRD'),
('Sailor', 'You have spent years aboard a ship, learning the ways of the sea. You know how to navigate, read weather, and survive storms.', ARRAY['Athletics', 'Perception'], ARRAY['Navigators tools', 'Vehicles (water)'], 0, 'Belaying pin, silk rope, lucky charm, common clothes', 1000, 'Ships Passage', 'You can secure free passage on a ship for yourself and your companions. You might be expected to assist the crew.', ARRAY['My friends know they can rely on me', 'I work hard so I can play hard', 'I enjoy sailing into new ports', 'I stretch the truth to tell a good story'], ARRAY['Respect: Everyone deserves respect', 'Fairness: We all do the work so we all share the reward', 'Freedom: The sea is freedom', 'Mastery: I am a predator and ships are my prey'], ARRAY['I am loyal to my captain first', 'The ship is most important', 'I was cheated of my share once', 'Someday I will own my own ship'], ARRAY['I follow orders even when wrong', 'I make a point of my superiority', 'Once someone questions my courage I never back down', 'I cannot resist a wager'], ARRAY['sailor', 'travel'], 'SRD'),
('Soldier', 'You have trained as a soldier. You might have been part of a national army, mercenary company, or local militia.', ARRAY['Athletics', 'Intimidation'], ARRAY['Gaming set', 'Vehicles (land)'], 0, 'Insignia of rank, trophy from enemy, dice set, common clothes', 1000, 'Military Rank', 'You have a military rank from your career. Soldiers loyal to your former organization still recognize your authority.', ARRAY['I am always polite and respectful', 'I am haunted by memories of war', 'I face problems head-on', 'I enjoy being strong and breaking things'], ARRAY['Greater Good: Our lot is to lay down our lives', 'Responsibility: I do what I must and obey authority', 'Independence: When people follow blindly they embrace tyranny', 'Might: In life as in war the stronger force wins'], ARRAY['I would still lay down my life for those I served with', 'Someone saved my life on the battlefield', 'My honor is my life', 'I fight for those who cannot fight for themselves'], ARRAY['The monstrous enemy we faced still leaves me quivering', 'I have little respect for anyone not a warrior', 'A shameful mistake cost many lives', 'My hatred for my enemies is blind'], ARRAY['military', 'combat'], 'SRD'),
('Urchin', 'You grew up on the streets alone, orphaned, and poor. You survived through wit, theft, and street smarts.', ARRAY['Sleight of Hand', 'Stealth'], ARRAY['Disguise kit', 'Thieves tools'], 0, 'Small knife, map of home city, pet mouse, token to remember parents, common clothes', 1000, 'City Secrets', 'You know the secret patterns and flow of cities. You can find passages through the urban sprawl that others would miss.', ARRAY['I hide scraps of food and trinkets away', 'I ask a lot of questions', 'I like to squeeze into small places', 'I sleep with my back to a wall'], ARRAY['Respect: All people deserve to be treated with dignity', 'Community: We have to take care of each other', 'Change: The low can be lifted up', 'Retribution: The rich need to be shown their money is not everything'], ARRAY['I owe my survival to someone who taught me to live on the streets', 'I owe a debt I can never repay', 'I escaped my life of poverty by robbing an important person', 'No one else should have to endure what I did'], ARRAY['I will never fully trust anyone other than myself', 'I remember every insult', 'I have trouble following plans', 'I turn tail when things look bad'], ARRAY['street', 'survival'], 'SRD')
